mapper.insert:
INSERT INTO
    user (
        email
        , password
        , nickname
        , position_id
        , image_id
        , level
        , create_date
    )
    VALUES(
        #{email}
        , #{password}
        , #{nickName}
        , #{position.positionId}
        , #{image.imageId}
        , #{level}
        , #{createDate}
    )

mapper.update:
UPDATE user
SET update_date = #{updateDate}
        , nickname = #{nickName}
        , position_id = #{position.positionId}
        , image_id = #{image.imageId}
        , password = #{password}
        , reset_token = #{resetToken}
        , status = #{status}
        , level = #{level}

WHERE user_id = #{userId}
